import React, { useState } from "react";
import './style.css';
import {auth} from '../../config/firebase';
import { Link,  } from "react-router-dom";
import { Redirect } from 'react-router'

const Login = () => {

  const[email, setEmail]= useState("");
  const[senha, setSenha]= useState("");

  const chamaLogin = () => {
    this.setState({
      redirect: true
    })
   }

  const logar = async () => {
    try {
    const user = await auth.signInWithEmailAndPassword(email, senha);
    console.log(user);
    
    } catch (error) {
      console.log(error.message)
    }
  }

  return (
    <div className="login">
      <h1 className="loginName">Login</h1>
      <form
        className="login__form"
        onSubmit={logar}
      >
        <label>Email</label>
        <input
          type="email"
          placeholder=""
          onChange={(event) => {setEmail(event.target.value)}}
        />

        <label>Senha</label>
        <input
          type="password"
          placeholder=""
          onChange={(event) => {setSenha(event.target.value)}}
        />

        <div className="cadastro-link">
          <Link to="./Signup" className="textoConta">NÃ£o tem uma conta? <span>Cadastre-se.</span></Link>
        </div>

        <input type="submit" value="Login" onClick={chamaLogin}/>

      </form>
    </div>
  );
};

export default Login;
