import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import './styles.css';
import firebaseDb from '../../config/firebase.js';
import { toast } from 'react-toastify';

const AddEdit = () => {
  const initialState = {
    veiculo: "",
    proprietario: "",
    ano: "",
    telefone: "",
    placa: "",
    valorServico: "",
    horarioEntrada: "",
    descricao: "",
  };
  const [state, setState] = useState(initialState);
  const [data, setData] = useState({});
  const { veiculo, proprietario, ano, telefone, placa, valorServico, horarioEntrada, descricao} = state;

  const history = useHistory();

  const { id } = useParams();
  useEffect(() => {
    firebaseDb.child("contacts").on("value", (snapshot) => {
      if (snapshot.val() !== null) {
        setData({
          ...snapshot.val(),
        });
      } else {
        setData({});
      }
    });
    return () => {
      setData({});
    };
  }, [id]);

  useEffect(() => {
    if (id) {
      setState({ ...data[id] });
    } else {
      setState({ ...initialState });
    }
    return () => {
      setState({ ...initialState });
    };
  }, [id, data]);

  const handleInputChange = (e) => {
    let { name, value } = e.target;
    setState({
      ...state,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!veiculo || !proprietario || !ano || !telefone || !placa || !valorServico || !horarioEntrada || !descricao) {
      toast.error("Campos faltando");
    } else {
      if (!id) {
        // No id mean user is adding record for the first time
        firebaseDb.child("contacts").push(state, (err) => {
          if (err) {
            toast.error(err);
          } else {
            toast.success("Veículo adicionado com sucesso");
          }
        });
      } else {
        firebaseDb.child(`/veiculos/${id}`).set(state, (err) => {
          if (err) {
            toast.error(err);
          } else {
            toast.success("");
          }
        });
      }
      setTimeout(() => history.push("/"), 500);
    }
  };
  return (
    <div className="add-edit">
      <form
        className="add-edit__form"
        onSubmit={handleSubmit}
      >
        <label>Veículo:</label>
        <input
          type="text"
          id="name"
          name="name"
          placeholder=""
          value={veiculo || ""}
          onChange={handleInputChange}
        />

        <label htmlFor="email">Proprietario:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={proprietario || ""}
          onChange={handleInputChange}
        />

        <label htmlFor="contact">Ano:</label>
        <input
          type="number"
          id="ano"
          name="ano"
          placeholder=""
          value={ano || ""}
          onChange={handleInputChange}
        />

        <label htmlFor="name">Telefone:</label>
        <input
          type="number"
          id="contact"
          name="contact"
          placeholder=""
          value={telefone || ""}
          onChange={handleInputChange}
        />

        <label htmlFor="name">Placa:</label>
        <input
          type="text"
          id="contact"
          name="contact"
          placeholder=""
          value={placa || ""}
          onChange={handleInputChange}
        />

        <input type="submit" value={id ? "Update" : "Save"} />
      </form>
    </div>
  );
};

export default AddEdit;
