import React, { useState } from "react";
import './style.css';
import {auth} from '../../config/firebase';
import { Link, useHistory } from "react-router-dom";

const Login = () => {

  const[email, setEmail]= useState("");
  const[senha, setSenha]= useState("");

  const navigate = useHistory();

  function authenticateUser() {
    auth
    .get("", {params: {
      email: email,
      senha: senha
}})
    .then((response) => {
        console.log(response.data)
        if(response.data.length == 0)
            alert("")
        else {
            alert("Usuario logado!")
            navigate.push("/CadCartao");
        }
    }
    )
    .catch((err) => {
      console.error("ops! ocorreu um erro" + err);
    });
}

  const logar = async () => {
    try {
    const user = await auth.signInWithEmailAndPassword(email, senha);
    navigate.push("/home");
    console.log(user);
    } catch (error) {
      console.log(error.message)
    }
  }

  function handleLogin(e){
    logar();
    authenticateUser
}

  return (
    <div className="login">
      <h1 className="loginName">Login</h1>
      <form
        className="login__form"
        onSubmit={handleLogin}
      >
        <label>Email</label>
        <input
          type="email"
          placeholder=""
          required onChange={(event) => {setEmail(event.target.value)}}
        />

        <label>Senha</label>
        <input
          type="password"
          placeholder=""
          required onChange={(event) => {setSenha(event.target.value)}}
        />

        <div className="cadastro-link">
          <Link to="./Signup" className="textoConta">NÃ£o tem uma conta? <span>Cadastre-se.</span></Link>
        </div>

        <input type="submit" value="Login"/>

      </form>
    </div>
  );
};

export default Login;
