import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import './styles.css';
import firebaseDb from '../../config/firebase.js';
import { toast } from 'react-toastify';

const AddEdit = () => {
  const initialState = {
    name: "",
    email: "",
    contact: "",
  };
  const [state, setState] = useState(initialState);
  const [data, setData] = useState({});
  const { veiculo, proprietario, ano, telefone, placa, valorServico, horarioEntrada, descricao } = state;

  const history = useHistory();

  const { id } = useParams();
  useEffect(() => {
    firebaseDb.child("contacts").on("value", (snapshot) => {
      if (snapshot.val() !== null) {
        setData({
          ...snapshot.val(),
        });
      } else {
        setData({});
      }
    });
    return () => {
      setData({});
    };
  }, [id]);

  useEffect(() => {
    if (id) {
      setState({ ...data[id] });
    } else {
      setState({ ...initialState });
    }
    return () => {
      setState({ ...initialState });
    };
  }, [id, data]);

  const handleInputChange = (e) => {
    let { name, value } = e.target;
    setState({
      ...state,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!veiculo || !proprietario || !ano || !telefone || !placa || !valorServico || !horarioEntrada || !descricao) {
      toast.error("Please provide value in each input field");
    } else {
      if (!id) {
        firebaseDb.child("contacts").push(state, (err) => {
          if (err) {
            toast.error(err);
          } else {
            toast.success("User Added Successfully");
          }
        });
      } else {
        firebaseDb.child(`/contacts/${id}`).set(state, (err) => {
          if (err) {
            toast.error(err);
          } else {
            toast.success("User Updated Successfully");
          }
        });
      }
      setTimeout(() => history.push("/"), 500);
    }
  };
  return (
    <div className="add-edit">
      <form
        className="add-edit__form"
        onSubmit={handleSubmit}
      >
        <label htmlFor="veiculo">Veículo:</label>
        <input
          type="text"
          id="veiculo"
          name="veiculo"
          placeholder=""
          value={veiculo || ""}
          onChange={handleInputChange}
        />

        <label htmlFor="proprietario">Proprietário:</label>
        <input
          type="text"
          id="propietario"
          name="proprietario"
          placeholder=""
          value={proprietario || ""}
          onChange={handleInputChange}
        />

        <label htmlFor="ano">Ano:</label>
        <input
          type="number"
          id="ano"
          name="ano"
          placeholder=""
          value={ano || ""}
          onChange={handleInputChange}
        />

        <label htmlFor="horarioEntrada">Telefone:</label>
        <input
          type="number"
          id="telefone"
          name="telefone"
          placeholder=""
          value={telefone || ""}
          onChange={handleInputChange}
        />

        <input type="submit" value={id ? "Atualizar" : "Cadastrar"} />
      </form>
    </div>
  );
};

export default AddEdit;
